import React from "react";
import Sidebar from "../Sidebar";
import Topbar from "../Topbar";
import { useNavigate } from "react-router-dom";
import config from './../../helpers/config.json';

const UsersEdit = () => {
    let navigate = useNavigate();
    let productData = JSON.parse(sessionStorage.getItem("product")); 
    const cancel = () => {
        var {userName, nickname, password, level, active} = document.forms[0]; 
        var hasChanges = userName.value.length > 0 ||  nickname.value.length > 0 || password.value.length > 0 || level.value.length > 0 || active.value.length > 0;
        if(hasChanges){
            if(window.confirm("Existen cambios sin guardar. Â¿Seguro de querer cancelar?")){
                navigate("/users");
            }
        } else {
            navigate("/users")
        }
    }

    const save = async (event) => {
        event.preventDefault();
        var {userName, nickname, password, level, active} = document.forms[0];
        var errors = "";
        if(errors.length > 0){
            window.alert("Corrija los siguientes errores:\n"+errors);
        } else {
            const requestOptions = {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json'},
                body: JSON.stringify({ "operatorId": config.operatorId, "name": userName.value,"nickname": nickname.value,"password": password.value,"level": level.value, "active": usersData.active})
              }
              fetch(config.apiURL+"users/"+usersData.id, requestOptions).then((response) => {
                switch(response.status){
                  case 400:
                    console.log("consulta mal formada");
                    break;
                }
                return response.json();
              }).then((result) => {
                  window.alert("Actualizacion existosa");
                  navigate("/users");
              })
        }
    }
    return (<div>
            <Topbar />
            <Sidebar />
            <div className="content-wrapper">
                <section className="content-header">
                    <div className="container-fluid">
                        <div className="row mb-2">
                            <div className="col-sm-6">
                                <h1>IncorporaciÃ³n de Producto</h1>
                            </div>
                            <div className="col-sm-6">
                                <ol className="breadcrumb float-sm-right">
                                    <li className="breadcrumb-item"><a href="/">Cloud Sales</a></li>
                                    <li className="breadcrumb-item"><a href="/users">Productos</a></li>
                                    <li className="breadcrumb-item active">Agregar</li>
                                </ol>
                            </div>
                        </div>
                    </div>
                </section>
                <section className="content">
                    <div className="card">
                        <div className="card-body">
                            <form onSubmit={save}>
                            <div className="row">
                                <div className="col-12">
                                    <div className="form-group">
                                        <label htmlFor="userName" className="control-label">Nombre</label>
                                        <input type="text" name="userName" id="userName" className="form-control" defaultValue={productData.name} required />
                                    </div>
                                </div>
                                <div className="col-4">
                                    <div className="form-group">
                                        <label htmlFor="nickname" className="control-label">nickname</label>
                                        <input type="number" name="nickname" id="nickname" className="form-control" defaultValue={productData.nickname} required />
                                    </div>
                                </div>
                                <div className="col-4">
                                    <div className="form-group">
                                        <label htmlFor="password" className="control-label">contraseÃ±a</label>
                                        <input type="number" name="password" id="password" className="form-control" defaultValue={productData.password} required />
                                    </div>
                                </div>
                                <div className="col-4">
                                    <div className="form-group">
                                        <label htmlFor="level" className="control-label">level</label>
                                        <input type="number" name="level" id="level" className="form-control" defaultValue={productData.level} required />
                                    </div>
                                </div>
                                <div className="col-6">
                                    <div className="form-group">
                                        <label htmlFor="active" className="control-label">Maximo Descuento Porcentaje</label>
                                        <input type="number" name="active" id="active" defaultValue={usersData.active} className="form-control" required />
                                    </div>
                                </div>
                            </div>
                            <div className="d-flex justify-content-between">
                                <button type="button" onClick={cancel} className="btn btn-secondary"><i className="fas fa-times"></i> Cancelar</button>
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Guardar</button>
                            </div>
                            </form>
                        </div>
                    </div>
                </section>
            </div>
        </div>
    )
}

export default UsersEdit;
